DELIMITER // CREATE PROCEDURE testCustomErrors(IN value INT)
BEGIN IF value = 1 THEN SIGNAL SQLSTATE '45000' SET MESSAGE_TEXT = 'ì—ëŸ¬: ì…ë ¥ê°’ì´ 1ì…ë‹ˆë‹¤.'; ELSEIF value = 2 THEN SIGNAL SQLSTATE '45001' SET MESSAGE_TEXT = 'ì—ëŸ¬: ì…ë ¥ê°’ì´ 2ì…ë‹ˆë‹¤.'; ELSEIF value = 3 THEN SIGNAL SQLSTATE '45002' SET MESSAGE_TEXT = 'ì—ëŸ¬: ì…ë ¥ê°’ì´ 3ì…ë‹ˆë‹¤.'; ELSE SELECT 'ì •ìƒì ì¸ ì…ë ¥ê°’ì…ë‹ˆë‹¤.' AS message; END IF;
